(()=>{let e=document.querySelector(".image");(async()=>{const a=await fetch("http://api.openweathermap.org/data/2.5/weather?q=London&appid=4afc0e7848e13a7ccc62b3be90d8b38f"),t=await a.json(),o=(t.main.temp-273.15).toFixed(2),n=t.main.humidity,c=t.weather[0].description,i=t.weather[0].icon,p=(await fetch("http://openweathermap.org/img/wn/"+i+"@2x.png")).url;e.src=p,console.log(o),console.log(n),console.log(c)})()})();